//data input into varriable for change DOM
let img = document.getElementById("img");
let author = document.getElementById("author");
let job = document.getElementById("job");
let profile = document.getElementById("profile");

let currentItem = 0;

//first type of load array in html
// window.addEventListener("DOMContentLoaded", function(){

//     showData();

// })

//second type of load array in html
// window.addEventListener("DOMContentLoaded", ()=>{

//     showData();

// })

//Thired type of load array in html
function onLoad() {
    showData();
};
window.addEventListener("DOMContentLoaded", onLoad);


//array data input html varriabls.
function showData() {
    let item = users[currentItem];
    img.src = item.img;
    author.innerHTML = item.name;
    job.innerHTML = item.job;
    profile.innerHTML = item.Text;
};

function nextBtn() {
    currentItem++;
    if (currentItem > users.length - 1) {
        currentItem = 0;
    }
    showData();
    return;
};

function preBtn() {
    currentItem--;
    if (currentItem < 0) {
        currentItem = users.length - 1;
    }
    showData();
    return;
};

function surpriseBtn() {
    currentItem = Math.floor(Math.random() * users.length);
    showData();
};